import React from 'react';
import { connect } from 'redux-bundler-react';
class Home extends React.Component {
  constructor(props){
    super(props);
    this.renderMsg = this.renderMsg.bind(this);
  }
  renderMsg(){
    const {authIsLoggedIn, authUsername} = this.props;
    if(authIsLoggedIn){
      return (
        <p>{ authUsername }</p>
      )
    }else{
        return <p>Click the Login button below to log in.</p>
    }
  }
  render(){

    const { doAuthLogin, doAuthLogout, authIsLoggedIn} = this.props;
    return (
      <div className="container">
        <div className="card bg-light m-5">
          <div className="card-body">
            <h2>Welcome to Share map</h2>
            {
              this.renderMsg()
            }
            <button onClick={ doAuthLogin } disabled ={ authIsLoggedIn} className="btn btn-primary">Login</button>
            <button onClick={ doAuthLogout } disabled ={ !authIsLoggedIn} className="btn btn-secondary">Logout</button>
          </div>
        </div>
      </div>
    )
  }
}

export default connect(
  'doAuthLogin',
  'doAuthLogout',
  'selectAuthIsLoggedIn',
  'selectAuthUsername',
  Home
  );
