import React from 'react';
import { connect } from 'redux-bundler-react';
import ToggleButton from './toggle-button';
import BaseMapSwitcher from './basemap-switcher';
import basemapSwitcher from './basemap-switcher';

class Toolbar extends React.Component{
    render(){
        const { drawPoints, drawLines, drawPolygons, modify, drawSelect } = this.props;
        const tools = [{
            toolTip: "Select Features",
            className: "ms ms-select-arrow",
            interaction: drawSelect
        },{
            toolTip: "Draw some points",
            className: "ms ms-points",
            interaction: drawPoints
        },{
            toolTip: "Draw a line",
            className: "ms ms-line",
            interaction: drawLines
        },{
            toolTip: "Draw a polygon",
            className: "ms ms-polygon",
            interaction: drawPolygons
        },{
            toolTip: "Modify Features",
            className: "ms ms-pencil",
            interaction: modify
        }
    
    ]
        return (
            <div className="container-fluid mt-3" style={{position:'absolute',zIndex:2}}>
                <div className="float-right">
                    <BaseMapSwitcher/>
                    <a href="/" className="btn btn-info">Go Home</a>
                </div>
                <div className="float-left">
                {
                    tools.map((item, i) => {
                        return(
                            <ToggleButton
                            key = {i}
                            toolTip = {item.toolTip}
                            className = {item.className}
                            interaction = {item.interaction}
                            />
                        )
                    })
                }
                </div>
            </div>
        )
    }
}
export default connect(
    'selectDrawPoints',
    'selectDrawLines',
    'selectDrawPolygons',
    'selectModify',
    'selectDrawSelect',
    Toolbar
);
