import React from 'react';
import { connect } from 'redux-bundler-react';
import classnames from 'classnames';
class DownloadButton extends React.Component{
    constructor(props){
        super(props)
        this.handleClick = this.handleClick.bind(this)
    }
    handleClick(){
        const { doDrawDownloadFeatures} = this.props;
        doDrawDownloadFeatures();
    }
    render(){
        const {className, toolTip, interaction, drawActiveInteraction} = this.props
        const active = interaction === drawActiveInteraction;
        const btnClass = classnames({
            "btn": true,
            "btn-outline-info": !active,
            "btn-outline-warning": active,
            "mr-2": true
        })
        return (
            <button onClick={ this.handleClick } className={ btnClass} title= {toolTip}>
                <i className={ className}></i>
            </button>
        )
    }
}

export default connect(
    DownloadButton
);
