import React from 'react';
import { connect } from 'redux-bundler-react';
import classnames from 'classnames';
class LeaderboardCard extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            isExpanded: false
        }
        this.toggleExpanded = this.toggleExpanded.bind(this)
        this.renderBody = this.renderBody.bind(this)
    }
    toggleExpanded(){
        this.setState({
            isExpanded: !this.state.isExpanded
        })
    }
    renderBody(){
        const { isExpanded } = this.state;
        if(!isExpanded) return null;
        return (
            <div className="card-body">
                <h4 className="card-title">Primary card title</h4>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
            </div>
        )
    }
    render(){
        const { isExpanded} = this.state;
        const expanderClass = classnames({
            "ms": true,
            "ms-plus": !isExpanded,
            "ms-minus": isExpanded
        })
        return(
            <div className="card text-white bg-info ml-3" style={{maxWidth: '20rem', position: 'absolute', top: '60px', zIndex:'2'}}>
                  <div className="card-header" onDoubleClick={this.toggleExpanded}>
                  <span className= "float-right" onClick={this.toggleExpanded}>
                    <i className={ expanderClass }></i>
                  </span>
                  Leaderboard
                  </div>
                {
                    this.renderBody()
                }
            </div>
        )
    }
}
export default connect(
    LeaderboardCard
)
