import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Draw from 'ol/interaction/Draw';
import Modify from 'ol/interaction/Modify';
import Select from 'ol/interaction/Select';
import Style from 'ol/style/Style';
import Fill from 'ol/style/Fill';
import Stroke from 'ol/style/Stroke';
import {debounce} from 'lodash';

export default {
    name: 'draw',
    getReducer:() =>{
        const initialData = {
            layer: null,
            shouldInitalize: false,
            drawPolygons: null,
            modify:null,
            select:null,
            fillColor:`rgba(249, 4, 4,.5)`,
            strokeColor:`rgba(249, 4, 4,.9)`,
            strokeWidth:3,
            activeInteraction: null
        }
        return (state = initialData, {type, payload}) => {
            switch(type){
                case 'DRAW_INITIALIZE_STARTED':
                case 'DRAW_INITIALIZE_FINISHED':
                case 'DRAW_ADD_INTERACTION':
                    return Object.assign({}, state, payload);
                case "MAP_INITIALIZED":
                    return Object.assign({}, state, {
                        shouldInitalize: true
                    });
                default:
                    return state;
            }
        }
    },
    doDrawInitializeLayer: () => ({dispatch, store}) => {
        dispatch({type: 'DRAW_INITIALIZE_STARTED', payload: {shouldInitalize: false}})
        const map = store.selectMap();
        const styleSettings = store.selectDrawStyleSettings();
        const source = new VectorSource();
        source.on('addfeature', store.doDrawDataHandleAdd);
        source.on('removefeature', store.doDrawDataHandleRemove);
        //source.on('changefeature', debounce(store.doDrawDataHandleChange,200));
        const layer = new VectorLayer({
            source: source,
            zIndex: 10,
            style: new Style({
                fill: new Fill({
                    color: styleSettings.fillColor
                }),
                stroke: new Stroke({
                    width: styleSettings.strokeWidth,
                    color: styleSettings.strokeColor
                })
            })
        })
        const drawPolygons = new Draw({
            source: source,
            type: 'Polygon'
        })
        const modify = new Modify({
            source: source
        })
        const select = new Select({
            layers: [layer]
        })
        map.addLayer(layer);
        //map.addInteraction(modify);
        dispatch({
            type: 'DRAW_INITIALIZE_FINISHED',
            payload:{
                layer: layer,
                drawPolygons: drawPolygons,
                modify: modify,
                select: select
            }
        })
    },
    doDrawAddInteraction: (interaction) => ({dispatch, store}) =>{
        const map = store.selectMap();
        const select = store.selectDrawSelect();
        select.getFeatures().clear();
        const activeInteraction = store.selectDrawActiveInteraction();
        let newInteraction = null;
        map.removeInteraction(activeInteraction)
        if(interaction!==activeInteraction){
            map.addInteraction(interaction);
            newInteraction = interaction;
        } 
        dispatch({
            type: 'DRAW_ADD_INTERACTION',
            payload:{
                activeInteraction: newInteraction
            }
        })
    },
    doDrawDeleteSelectedFeatures:() =>({dispatch, store}) =>{
        const layer = store.selectDrawLayer();
        const source = layer.getSource();
        const select = store.selectDrawSelect();
        const features = select.getFeatures();
        features.forEach((feature)=>{
            source.removeFeature(feature);
        })
        features.clear();
    },

    selectDrawLayer: (state) => {
        return state.draw.layer;
    },
    selectDrawPolygons: (state) =>{
        return state.draw.drawPolygons;
    },
    selectModify: (state) => {
        return state.draw.modify;
    },
    selectDrawSelect: (state) => {
        return state.draw.select;
    },
    selectDrawActiveInteraction: (state) => {
        return state.draw.activeInteraction;
    },
    selectDrawStyleSettings:(state) =>{
        return {
            fillColor: state.draw.fillColor,
            strokeColor: state.draw.strokeColor,
            strokeWidth:state.draw.strokeWidth,
        }

    },
    reactDrawShouldInitialize: (state) => {
        if(state.draw.shouldInitalize) return { actionCreator: 'doDrawInitializeLayer'}
    },
    init: (store) =>{
        window.addEventListener('keyup', (e) =>{
            if(e.keyCode === 8 || e.keyCode === 46){
                store.doDrawDeleteSelectedFeatures();
            }
        })
    }
}
