import React from 'react';
import {connect } from 'redux-bundler-react';

class DeleteButton extends React.Component{
    constructor(props){
        super(props)
        this.state ={
            isConfirming: false
        }
        this.renderDefault = this.renderDefault.bind(this)
        this.toggleConfirming = this.toggleConfirming.bind(this)
        this.renderConfirm = this.renderConfirm.bind(this)
    }
    toggleConfirming(fn){
        this.setState({
            isConfirming: !this.state.isConfirming
        }, () => {if(typeof fn === 'function') fn();})
    }
    renderDefault(){
        return <button className="btn btn-info" title="delete all your stuff" onClick={this.toggleConfirming}>Delete Feature</button>
    }
    renderConfirm(){
        const { doDrawDataBulkDelete } = this.props
        return (
            <>
            <button className="btn btn-primary" title="The indecisive option." onClick={this.toggleConfirming}>Deny It All</button>
            <button className="btn btn-secondary" title="the I really do want to do this way out" onClick={() =>{this.toggleConfirming(doDrawDataBulkDelete)}}>Confirm</button>
        </>
        )
    }
    render(){
            const { isConfirming } = this.state;
            if( isConfirming){
                return this.renderConfirm();
            }else{
                return this.renderDefault();
            }
    }
}
export default connect(
    'doDrawDataBulkDelete',
    DeleteButton
);
