import React from 'react';
import {connect} from 'redux-bundler-react';
import classnames from 'classnames';

class BaseMapSwitcher extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            expanded: false
        }
        this.toggleExpanded = this.toggleExpanded.bind(this);
    }
    toggleExpanded(){
        this.setState({
            expanded: !this.state.expanded
        })
    }


    render(){
        const {expanded} = this.state;
        const { basemap, doBasemapsChange} = this.props;
        const dropdownStyle = {
            position: 'absolute',
            top:0,
            left:0,
            transform: 'translate3d(0px, 35px, 0px'
        }
        const containerClass = classnames({
            "btn-group": true,
            "show" : expanded,
            "mr-2" : true
        })
        const dropdownClass = classnames({
            "dropdown-menu": true,
            "show" : expanded,
        })
        return (

            <div className={containerClass} >
                <button type="button" className="btn btn-info dropdown-toggle" onClick={this.toggleExpanded}>
                    BaseMap
                </button>
                <div className={dropdownClass} style = { dropdownStyle}>
                    {
                        basemaps.map((basemap, i) => {
                            return (
                                <div
                                    key= { basemap.id}
                                    className= "dropdown-item"
                                    onClick={() =>{
                                        doBasemapsChange(i);
                                    }}
                                    >
                                    { basemap.name}
                                    </div>
                            )
                        })
                    }
                </div>
            </div>
        )
    }
}
export default connect(
    'selectBasemap',
    'doBasemapsChange',
    BaseMapSwitcher
)
