import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Draw from 'ol/interaction/Draw';

export default {
    name: 'draw',
    getReducer:() =>{
        const initialData = {
            layer: null,
            shouldInitalize: false,
            drawPoints: null,
            drawLines: null,
            drawPolygons: null,
            activeInteraction: null
        }
        return (state = initialData, {type, payload}) => {
            switch(type){
                case "DRAW_INITIALIZE_STARTED":
                case "DRAW_INITIALIZE_FINISHED":
                    return Object.assign({}, state, payload);
                case "MAP_INITIALIZED":
                    return Object.assign({}, state, {
                        shouldInitalize: true
                    });
                default:
                    return state;
            }
        }
    },
    doDrawInitializeLayer: () => ({dispatch, store}) => {
        dispatch({type: 'DRAW_INITIALIZE_STARTED', payload: {shouldInitalize: false}})
        const map = store.selectMap();
        const source = new VectorSource();
        const layer = new VectorLayer({
            source: source
        })
        const drawPoints = new Draw({
            source: source,
            type: 'Point'
        })
        const drawLines = new Draw({
            source: source,
            type: 'LineString'
        })
        const drawPolygons = new Draw({
            source: source,
            type: 'Polygon'
        })
        map.addLayer(layer);
        map.addInteraction(drawPoints);
        map.addInteraction(drawLines);
        map.addInteraction(drawPolygons);
        dispatch({
            type: 'DRAW_INITIALIZE_FINISHED',
            payload:{
                layer: layer,
                drawPoints: drawPoints,
                drawLines: drawLines,
                drawPolygons: drawPolygons
            }
        })
    },
    doDrawAddInteraction: (interaction) => ({dispatch, store}) =>{
        const map = store.selectMap();
        const activeInteraction = store.selectDrawActiveInteraction();
        map.removeInteraction(activeInteraction)
        if(interaction!==activeInteraction) map.addInteraction(interaction);
    },
    selectDrawLayer: (state) => {
        return state.draw.layer;
    },
    selectDrawPoints: (state) => {
        return state.draw.drawPoints;
    },
    selectDrawLines: (state) => {
        return state.draw.drawLines;
    },
    selectDrawPolygons: (state) => {
        return state.draw.drawPolygons;
    },
    selectDrawActiveInteraction: (state) => {
        return state.draw.activeInteraction;
    },
    reactDrawShouldInitialize: (state) => {
        if(state.draw.shouldInitalize) return { actionCreator: 'doDrawInitializeLayer'}
    }
}
