import {transform} from 'ol/proj';
import census from 'citysdk';
import GeoJSON from 'ol/format/GeoJSON';
import * as turf from '@turf/turf';
const geoJSON = new GeoJSON();


export default {
    name: 'selectByFips',
    getReducer:() =>{
        const initialData = {
            selectByFipsIsActive: false,
        }
        return (state = initialData, {type, payload}) => {
            switch(type){
                case 'SELECT_BY_FIPS_ACTIVATED':
                case 'SELECT_BY_FIPS_DEACTIVATED':
                return Object.assign({}, state, payload);
                case 'SELECT_BY_FIPS_STARTED':
                case 'SELECT_BY_FIPS_GET_FIPS':
                case 'SELECT_BY_FIPS_GET_STATS':
                case 'SELECT_BY_FIPS_FINISHED':
                default:
                    return state;
            }
        }
    },
    doSelectByFips: (e) =>({dispatch, store}) =>{
        dispatch({
            type: "SELECT_BY_FIPS_STARTED"
        })
        var statsKeyStore = store.selectCitysdkStatsKey()
        const layer = store.selectDrawLayer();
        const source = layer.getSource();

        //it seems like i refactored, but i dont remember what I did. this looks like a click handler 
        //e is now undef - so i probably need to assign a click and properly remove it.
        var coord = transform(e.coordinate,'EPSG:3857','EPSG:4326')
        var coordOut = false;
        const pointInPoly = x => !x.getGeometry().intersectsCoordinate(e.coordinate)
        if(source.getFeatures().length!=0){
            
            if(source.getFeatures().every(pointInPoly)){
                //coordOut = true
            }else{
                coordOut = true;
            }
        }

        if(!coordOut){
            //use turf to see if layer already contains the point (or go through the feature attributes to see if the fips already has been clicked.)
            dispatch({
                type: "SELECT_BY_FIPS_GET_FIPS"
            })

            //need to parralell fetch stats from the nsi api
            census({
                "vintage" : 2015,    // required
                "geoHierarchy" : {   // required
                    "state" : {
                    "lat" : coord[1], 
                    "lng" : coord[0]
                    }
                }
                }, 
                (err, res) => {
                    if(err){

                    }else{
                    dispatch({
                        type: 'SELECT_BY_FIPS_STARTED'
                    })
                    census({
                        "vintage" : "2015",
                        "geoHierarchy" : {
                            "state": res.geoHierarchy.state,
                            //"county": res.geoHierarchy.county
                        },
                        "sourcePath" : ["acs", "acs5"],  
                        "values" : ["B19083_001E"],    // GINI index             
                        "statsKey" : statsKeyStore,
                        "geoResolution" : "5m"
                    },
                    (err, res) => {
                        if(err){

                        }else{
                            const features = geoJSON.readFeatures(res,{"dataProjection" : 'EPSG:4326', "featureProjection":'EPSG:3857', "id_": res.features[0].properties.STATEFP });
                            source.addFeatures(features);
                            dispatch({
                                type: 'SELECT_BY_FIPS_FINISHED'
                            })
                        }

                    }
                )
                }
            })
            dispatch({
                type: "SELECT_BY_FIPS_FINISHED"
            })            
        }else{
            dispatch(
                {
                    type: "SELECT_BY_FIPS_FINISHED"
                }
            )
        }
            
    },
    doDrawToggleSelectState: () =>({dispatch, store}) =>{

        const map = store.selectMap();
        //what if the active interaction is the select by fips?
        if (store.selectSelectByFipsIsActive()){
            map.un("click", store.doSelectByFips)
            dispatch({
                type: 'SELECT_BY_FIPS_DEACTIVATED',
                payload:{
                    selectByFipsIsActive: false,
                }
            })
        }else{
            map.on("click", store.doSelectByFips)
            dispatch({
                type: 'SELECT_BY_FIPS_ACTIVATED',
                payload:{
                    selectByFipsIsActive: true,
                }
            })
        }

    }
    ,
    selectSelectByFipsIsActive: (state) =>{
        return state.selectByFips.selectByFipsIsActive;
    }
}
