import GeoJSON from 'ol/format/GeoJSON';
const geoJSON = new GeoJSON();
export default{
    name: 'drawData',
    getReducer: () =>{
        const initialData = {
            shouldFetch: false,
            shouldCalculateStats: false
        }
        return (state = initialData, { type, payload}) => {
            switch(type){
                case 'DRAW_DATA_FETCH_STARTED':
                    return Object.assign({}, state, payload)
                case 'DRAW_INITIALIZE_FINISHED':
                    return Object.assign({}, state, {
                        shouldFetch: true
                    })
                default:
                    return state;
            }
        }
    },
    doDrawDataFetch: () => ({ dispatch, store, apiGet}) => {
        dispatch({type: 'DRAW_DATA_FETCH_STARTED', payload: { shouldFetch: false}})
        apiGet('/mydata',(err,response,body)=>{
            if(err || response.statusCode !== 200){
                console.error('ERROR', response);
            }else{
                const layer = store.selectDrawLayer();
                const source = layer.getSource();
                const features = geoJSON.readFeatures(body);
                source.addFeatures(features);
            }
        })
        dispatch({type: 'DRAW_DATA_FETCH_FINISHED', payload: {
            shouldCalculateStats: true
        }})
    },
    doDrawDataHandleAdd:(e) => ({dispatch, store, apiPost}) => {
        if(!e.feature.getId()){
            const styleSettings = store.selectDrawStyleSettings();
            e.feature.set('style',styleSettings,true);
            e.feature.set('created_by', "Master Will",true)
            e.feature.set('create_date', new Date(),true)
            const geoJsonFeature = geoJSON.writeFeature(e.feature)
            apiPost('/data', JSON.parse(geoJsonFeature), (err, response, body) =>{
            if(err || response.statusCode !== 200){
                console.error('ERROR', response);
            }else{
                e.feature.setId(body.id)
            }
        })
        }
    },
    doDrawDataHandleRemove:(e) => ({dispatch, store, apiDelete}) => {
        const id = e.feature.getId();
        if(id){
            apiDelete(`/data/${id}`, (err, response, body) =>{
            if(err || response.statusCode !== 200){
                console.error('ERROR', response);
            }else{
                console.log(body)
            }
        })
        }
    },
    doDrawDataHandleChange:(e) => ({dispatch, store, apiPut}) => {
        const id = e.feature.getId();
        if(id){
            const geoJsonFeature = geoJSON.writeFeature(e.feature)
            apiPut(`/data/${id}`, JSON.parse(geoJsonFeature), (err, response, body) =>{
            if(err || response.statusCode !== 200){
                console.error('ERROR', response);
            }else{
                console.log(body);
            }
        })
        }
    },
    doDrawDataBulkDelete:()=>({dispatch, store}) =>{
        const layer = store.selectDrawLayer();
        const source = layer.getSource();
        source.clear();
    },
    doDrawDataCalculateStats:()=>({dispatch, store}) =>{
        const drawLayer = store.selectDrawLayer();
        const collabLayer = store.selectCollabLayer();

        if(!drawLayer || !collabLayer) return;
        const drawSource = drawLayer.getSource();
        const collabSource = collabLayer.getSource();
        const drawFeatures = drawSource.getFeatures();
        const collabFeatures = collabSource.getFeatures();
        const features = [...drawFeatures, ...collabFeatures];
        
        return features;
    },
    reactDrawDataShouldFetch:(state) =>{
        if(state.drawData.shouldFetch) return {actionCreator: 'doDrawDataFetch'}
    }
}
