import React from 'react';
import DeleteButton from '../delete-button';
import ToggleButton from '../toggle-button';

class AoiListItem extends React.Component{
    render(){
        const { userName, drawPolygons, modify, drawSelect } = this.props;
        const tools = [{
            toolTip: "Select Features",
            className: "ms ms-select-arrow",
            interaction: drawSelect
        },{
            toolTip: "Draw a polygon to define Area of Intrest (AOI)",
            className: "ms ms-polygon",
            interaction: drawPolygons
        },{
            toolTip: "Modify AOI Features",
            className: "ms ms-pencil",
            interaction: modify
        }
    
    ]

        return <li className="list-group-item-primary d-flex justify-content-between align-items-center">
            {userName}
            {tools.map((item, i) => {
                        return(
                            <ToggleButton
                            key = {i}
                            toolTip = {item.toolTip}
                            className = {item.className}
                            interaction = {item.interaction}
                            />
                        )
                    })
                }
        <span><button className="btn btn-info mr-2">Download </button></span>
        <span><DeleteButton/></span>
        </li>
    }
}
export default AoiListItem
