import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Style from 'ol/style/Style';
import Fill from 'ol/style/Fill';
import Stroke from 'ol/style/Stroke';
import Circle from 'ol/style/Circle';
import GeoJSON from 'ol/format/GeoJSON';
const geoJSON = new GeoJSON();

export default{
    name: 'NSI',
    getReducer:()=>{
        const initialData = {
            layer: null,
            lastMessage: null,
            shouldInitalize: false,
            shouldFetch: false,
            shouldHandleMsg: false,
            ready: false
        }
        return (state = initialData, {type, payload})=>{
            switch(type){
                case'NSI_INITIALIZE_STARTED':
                case'NSI_INITIALIZE_FINISHED':
                case 'NSI_FETCH_STARTED':
                    return Object.assign({}, state, payload)
                case 'WEBSOCKET_MESSAGE':
                return Object.assign({}, state, {
                    lastMessage: payload.e,
                    shouldHandleMsg: true
                });
                case "MAP_INITIALIZED":
                    return Object.assign({}, state, {
                        shouldInitalize: true
                    });
                default:
                    return state
            }
        }
    },    
    doNSIInitialize: () => ({dispatch, store}) => {
    dispatch({type: 'NSI_INITIALIZE_STARTED', payload: {shouldInitalize: false}})
    const map = store.selectMap();
    const source = new VectorSource();
    const layer = new VectorLayer({
        source: source,
        style: (feature, resolution )=>{
            const properties = feature.getProperties();
            const style = properties.style || {};
            return new Style({
            fill: new Fill({
                color: style.fillColor || `rgba( 66,232,244,.5)`
            }),
            stroke: new Stroke({
                width: style.strokeWidth || 3,
                color: style.strokeColor || `rgba( 66,232,244,.5)`
            }),
            image: new Circle({
                radius: style.circleRadius || 7,
                fill: new Fill({
                    color: style.circleFill || `rgba( 66,232,244,.5)`
                }),
                stroke: new Stroke({
                    width: style.circleStrokeWidth || 3,
                    color: style.circleFillColor || `rgba( 66,232,244,.5)`
                })
            })
        })
    }
    })
    map.addLayer(layer);
    dispatch({
        type: 'NSI_INITIALIZE_FINISHED',
        payload:{
            layer: layer,
            shouldFetch: true,
            ready: true
        }
    })
},
doNSIFetch: () => ({dispatch, store, apiGet}) => {
    dispatch({type: 'NSI_FETCH_STARTED', payload: {shouldFetch: false}})

    
    apiGet('/data',(err,response,body)=>{
        if(err || response.statusCode !== 200){
            console.error('ERROR', response);
        }else{
            const layer = store.selectNSILayer();
            const source = layer.getSource();
            const features = geoJSON.readFeatures(body);
            source.addFeatures(features);
            dispatch({type: 'DRAW_DATA_UPDATE_STATS', payload: {
                shouldCalculateStats: true
            }})
        }
    })
    dispatch({type: 'NSI_FETCH_FINISHED', payload: {}})
},
doNSIHandleMsg: () => ({dispatch, store}) => {
    dispatch({type: 'NSI_HANDLE_MSG_STARTED', payload:{shouldHandleMsg: false}})
    const ready = store.selectNSIReady();
    if(ready){
        const myEdipi = store.selectAuthEdipi();
        const lastMessage = store.selectNSILastMessage();
        const data = JSON.parse(lastMessage.data)
        if(myEdipi !== data.edipi){
            switch(data.action){
                case 'insert':
                    store.doNSIFetchFeatureById(data.id, data.edipi)
                    break;
                case 'delete':
                    store.doNSIDeleteFeatureById(data.id)
                    break;
                case 'update':
                    store.doNSIFetchFeatureById(data.id, data.edipi)
                    break;
                default:
            }
        }        
    }


},
doNSIFetchFeatureById: (id, edipi) => ({dispatch, store, apiGet}) =>{
    apiGet(`/data/${edipi}/${id}`, (err, response, body) =>{
        if(err || response.statusCode !== 200){
            console.error(response)
        }else{
            const layer = store.selectNSILayer();
            const source = layer.getSource();
            const feature = source.getFeatureById(id)
            if(feature){
                source.removeFeature(feature);
            }
            const newFeature = geoJSON.readFeature(body);
            source.addFeature(newFeature);
            dispatch({type: 'DRAW_DATA_UPDATE_STATS', payload: {
                shouldCalculateStats: true
            }})
        }
    })
},
doNSIDeleteFeatureById: (id, edipi) => ({dispatch, store, apiDelete}) =>{
            const layer = store.selectNSILayer();
            const source = layer.getSource();
            const feature = source.getFeatureById(id)
            if(feature){
                source.removeFeature(feature);
                dispatch({type: 'DRAW_DATA_UPDATE_STATS', payload: {
                    shouldCalculateStats: true
                }})
            }
},
selectNSILayer: (state) => {
    return state.NSI.layer;
},
selectNSILastMessage: (state) =>{
    return state.NSI.lastMessage;
},
selectNSIReady: (state) =>{
    return state.NSI.ready;
},
reactNSIShouldInitialize: (state) => {
    if(state.NSI.shouldInitalize) return { actionCreator: 'doNSIInitialize'}
},
reactNSIShouldFetch: (state) => {
    if(state.NSI.shouldFetch) return { actionCreator: 'doNSIFetch'}
},
reactorNSIShouldHandleMsg: (state) => {
    if(state.NSI.shouldHandleMsg) return {actionCreator: 'doNSIHandleMsg'}
}
}
